package tech.array;

public class ArrayEnhancedClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int arr[] = {10,20,30};
		
		for (int i : arr) {
			System.out.println(i);
		}
		
		for (int i=0; i < arr.length; i++) {
			System.out.println(arr[i]);
		}
	}

}
